import React from 'react';
import axios from 'axios';
const Them = () =>{
    const [Ten, setTen] = React.useState('');
    const [Linkimg, setLinkimg] = React.useState('');
    const [Gia, setGia] = React.useState('');
    const isChangeTen = val => setTen(val.target.value);
    const isChangeLink = val => setLinkimg(val.target.value);
    const isChangeGia = val =>setGia(val.target.value);
    const Submit = () =>{
        axios.post('/add', {
           ten : Ten,
           img : Linkimg,
           gia : Gia
          })
          .then(function (response) {
           alert(response)
          })
          .catch(function (error) {
            alert(error)
          });
     }
    return(
        <div >
           
                <div style={{margin: 30,width: '100%', height: '100px', display: 'flex', justifyContent: 'space-around'}}>
                <div style={{width: '30%', height: '60px'}}>
                    <label htmlFor>Nhập Tên Cây :</label>
                    <input type="text" style={{padding: 10, width: '90%'}} onChange ={(val) => isChangeTen(val)}  placeholder="Nhập Tên Cây Cảnh" />
                </div>
                <div style={{width: '30%', height: '60px'}}>
                    <label htmlFor>Nhập Link ảnh Cây :</label>
                    <input type="text" style={{padding: 10, width: '90%'}} onChange ={(val) => isChangeLink(val)} placeholder="Nhập Link ảnh Cây" />
                </div>
                <div style={{width: '30%', height: '60px'}}>
                    <label htmlFor>Nhập Gía :</label>
                    <input type="text" style={{padding: 10, width: '90%'}} onChange ={(val) => isChangeGia(val)} placeholder="Nhập Gía" />
                </div>
                </div>
            <input type="submit" value="Submit" onClick={() => Submit()} style={{padding: 12}}></input>
            
        </div>
    );
}
export default Them;
