const initstate = {
    giohang : [],
};

const reducers = (state = initstate, action) => {
    switch (action.type) {
        case 'Add_SanPham':
            console.log(state.giohang);
            return {...state, giohang : [...state.giohang, action.dulieu]} ;
      default:
        return state;
    }
  }
  
  export default reducers;
