const initstate = {
    giohang : [],
    tongtien : 0
};

const reducers = (state = initstate, action) => {
    switch (action.type) {
        case 'Add_SanPham':
            return {...state, giohang : [...state.giohang, action.dulieu]} ;
        case 'XoaSanPhamGioHang':
            return {...state, giohang : state.giohang.filter((item => item.id !== action.idxoa))}
        case 'TongTienGioHang':
            return {...state, tongtien : state.tongtien + parseFloat(action.tien)}
      default:
        return state;
    }
  }
  
  export default reducers;
