import React from 'react';
import { useDispatch, } from 'react-redux';
import '../../asset/CSS/Main.css';
import axios from 'axios';
const SanPham = ({ name, gia, img, id, item }) => {
  const dispatch = useDispatch();
  //const data = useSelector(state => state.dulieucaycanh);
  function numberWithCommas(x) {
    x = x.toString();
    var pattern = /(-?\d+)(\d{3})/;
    while (pattern.test(x))
      x = x.replace(pattern, "$1.$2");
    return x;
  }
  const XoaSubmit = (id) => {

    axios.post('http://localhost:5000/delete', {
      id: id
    })
      .then(function (response) {
        
        //dispatch({ type: 'Send_ID', id: id });
        console.log(response.data);
      })
      .catch(function (error) {
        alert(error);
      });

  }
  return (
    <div className="KhoiSanPham">
      <div className="card" style={{ width: '280px', height: 'auto', margin: 25 }}>
        <img className="card-img-top" src={img} alt="Card image cap" />
        <div className="card-body">
          <h5 className="card-title">{name}</h5>
          <p className="Gia" style={{ color: 'red', fontWeight: 'bold' }}> {numberWithCommas(gia)} VND</p>
          <div style={{ display: 'flex', justifyContent: 'space-around' }}>
            <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#exampleModalCenter" onClick={() => dispatch({ type: 'Add_SanPham', dulieu: item })}><i className="fas fa-cart-plus"></i></button>
            <button type="button" className="btn btn-success" data-toggle="modal" data-target="#exampleModal2" ><i className="far fa-trash-alt"></i></button>
            <button type="button" className="btn btn-info" data-toggle="modal" data-target="#modelId" onClick={() => dispatch({ type: 'DuLieuCanSua', dulieucansua: item, active: true })}><i className="fas fa-edit"></i></button>
          </div>
        </div>
      </div>
      <div className="modal fade" id="exampleModal2" aria-labelledby="exampleModalLabel" aria-hidden="true" tabIndex={-1} role="dialog">
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Xóa cây cảnh?</h5>
              <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">×</span>
              </button>
            </div>
            <div className="modal-body">
              <p>Bạn chắc chắn muốn xóa cây cảnh này ? </p>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-dismiss="modal">Hủy</button>
              <button type="button" className="btn btn-danger" data-dismiss="modal" onClick={() => XoaSubmit(id)}>Xóa bỏ</button>

            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
export default SanPham;
