import React from 'react';
import axios from 'axios';
import { useDispatch } from 'react-redux';
const Them = () => {
    const dispatch = useDispatch();
    const [Ten, setTen] = React.useState('');
    const [Linkimg, setLinkimg] = React.useState('');
    const [Gia, setGia] = React.useState('');
    const isChangeTen = val => setTen(val.target.value);
    const isChangeLink = val => setLinkimg(val.target.value);
    const isChangeGia = val => setGia(val.target.value);

    return (
        <div >
            <div className="container" style={{ marginTop: 50 }}>
                <form action="http://localhost:5000/add" method="post" enctype="multipart/form-data">
                <div className="row">
                    <div className="col-xl-4">
                        <div className="form-group">
                            <input type="text"  className="form-control"  aria-describedby="helpId" onChange={(val) => isChangeTen(val)} placeholder="Nhập Tên Cây Cảnh" />
                        </div>
                    </div>
                    <div className="col-xl-4">
                        <div className="form-group">
                          <label for="">Upload img : </label>
                          <input type="file" className="form-control-file" name="img"  aria-describedby="fileHelpId" />
                        </div>
                    </div>
                    <div className="col-xl-4">
                        <div className="form-group">
                            <input type="text"  className="form-control" aria-describedby="helpId" onChange={(val) => isChangeGia(val)} placeholder="Nhập Gía" />
                        </div>
                    </div>
                </div>
                <div className="container align-center">
                <button type="submit" className="btn btn-primary btn-lg" onClick={() => {
                    let dulieu = {id : Math.random(),name: Ten, img: Linkimg, Gia: Gia}
                    axios.post('http://localhost:5000/add', dulieu ).then(function (response) {
                            dispatch({type : 'DuLieuThem', data : JSON.parse(response.config.data)});
                        })
                        .catch(function (error) {
                            alert(error)
                        });
                }}>Thêm dữ liệu</button>
            </div>
            </form>
            </div>
            
        </div>
    );
}
export default Them;
