import React from 'react';
import Footer from "../FooterWeb.js/Footer";
import Menu from "../MenuWeb/Menu";
import Them from "./Backend/Them";
import Sua from "./Backend/Sua";
import Box from "./Box";
import Spinner from 'react-spinner-material';
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
const Home = () =>{
    const dispatch = useDispatch();
    const active = useSelector(state => state.active);
    const [Loading, setLoading] = React.useState(true);
    React.useEffect(() => {
        axios.get('http://localhost:5000/getdata').then(function (response) {
        
          dispatch({ type: 'DanhSachCayCanh', data: response.data });
          setLoading(false)
        })
          .catch(function (error) {
            // handle error
            console.log('Loi ' + error);
          });
      }, []);
   
    return(
        <div>
            
            <Spinner size={120} spinnerColor={"#333"} spinnerWidth={2} visible={true} />
            <Menu/>
            <Them/>
            { active == true ?  <Sua/> : <div /> }
            <Box />
            <Footer/>
        </div>
    );
}
export default Home;
