import React from 'react';
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
const Sua = () => {
    const dispatch = useDispatch();
    const [Ten, setTen] = React.useState(dulieucansua.name);
    const [Linkimg, setLinkimg] = React.useState('');
    const [Gia, setGia] = React.useState('');
    const isChangeTen = val => setTen(val.target.value);
    const isChangeLink = val => setLinkimg(val.target.value);
    const isChangeGia = val => setGia(val.target.value);
    const dulieucansua = useSelector(state => state.dulieucansua);
    React.useEffect(() =>{
        const dulieucansua = useSelector(state => state.dulieucansua);
        setdu
    })
    return (
        
        <div >
            <div className="container" style={{ marginTop: 50 }}>
                <div className="row">
                    <div className="col-xl-4">
                        <div className="form-group">
                            <input type="text"  className="form-control"  aria-describedby="helpId" onChange={(val) => isChangeTen(val)} value={Ten} placeholder="Nhập Tên Cây Cảnh" />
                        </div>
                    </div>
                    <div className="col-xl-4">
                        <div className="form-group">
                            <input type="text" className="form-control"aria-describedby="helpId" onChange={(val) => isChangeLink(val)} placeholder="Nhập Link ảnh Cây" />
                        </div>
                    </div>
                    <div className="col-xl-4">
                        <div className="form-group">
                            <input type="text"  className="form-control" aria-describedby="helpId" onChange={(val) => isChangeGia(val)} placeholder="Nhập Gía" />
                        </div>
                    </div>
                </div>
            </div>
            <div className="container align-center">
                <button type="button" className="btn btn-primary btn-lg" onClick={() => {
                    let dulieu = {id : Math.random(),name: Ten, img: Linkimg, Gia: Gia}
                    axios.post('http://localhost:5000/add', dulieu ).then(function (response) {
                            dispatch({type : 'DuLieuThem', data : JSON.parse(response.config.data)});
                        })
                        .catch(function (error) {
                            alert(error)
                        });
                }}>Chỉnh sửa dữ liệu</button>
            </div>
        </div>
    );
}
export default Sua;
