import React from 'react';
import '../../asset/CSS/Main.css';
const SanPham =({name, gia, img, id}) =>{
  const Xoa_Item = (id) =>{
    setdulieu(dulieu.filter(item => item.id !== id));
  }
  return(
    <div className="KhoiSanPham">
      <div style={{width: '280px', height: '380px', margin: 25}}>
        <div className="KhoiAnh" style={{width: '100%', height: '280px'}}>
          <a href><img src={img} width="100%" height="100%" alt="" /></a>
        </div>
        <div className="KhoiText">
          <h3><a >{name}</a></h3>
          <div className="Gia">
            {gia} VND
          </div>
          <div className="MuaHang">
            <a href><i className="fa fa-shopping-cart" />Mua</a>
            <a href onClick={() => Xoa_Item(id)}><i className=" fa fa-eye" />Xo√° </a>
          </div>
        </div>
      </div>
  </div>
  );
}
export default SanPham;
